use std::collections::BTreeMap;

pub struct Storage {
    items: BTreeMap<String, Vec<u8>>,
}

impl Storage {
    pub fn store(&mut self, key: String, data: Vec<u8>) {
        self.items.insert(key, data);
    }

    pub fn retrieve(&self, key: &str) -> Option<&Vec<u8>> {
        self.items.get(key)
    }
}

