#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_validate_email() {
        assert!(validate_email("test@example.com"));
        assert!(!validate_email("invalid"));
    }

    #[test]
    fn test_validate_password() {
        assert!(validate_password("password123"));
        assert!(!validate_password("short"));
    }
}
