mod auth;
mod database;
mod handlers;

fn main() {
    let db = database::connect();
    let auth_mgr = auth::Manager::new();
    handlers::start_server(db, auth_mgr);
}

files.src/auth.rs:
pub struct Manager {
    secret: String,
}

impl Manager {
    pub fn new() -> Self {
        Self {
            secret: std::env::var("SECRET").unwrap_or_default(),
        }
    }

    pub fn verify_token(&self, token: &str) -> bool {
        token == self.secret
    }
}

files.src/database.rs:
use std::collections::HashMap;

pub struct Connection {
    data: HashMap<String, String>,
}

pub fn connect() -> Connection {
    Connection {
        data: HashMap::new(),
    }
}

impl Connection {
    pub fn get(&self, key: &str) -> Option<&String> {
        self.data.get(key)
    }

    pub fn set(&mut self, key: String, value: String) {
        self.data.insert(key, value);
    }
}

files.src/handlers.rs:
use crate::auth::Manager;
use crate::database::Connection;

pub fn start_server(db: Connection, auth: Manager) {
    println!("Server starting...");
}

pub fn handle_request(auth: &Manager, token: &str) -> Result<String, String> {
    if auth.verify_token(token) {
        Ok("Authorized".to_string())
    } else {
        Err("Unauthorized".to_string())
    }
}
