pub struct Database {
    connection: Connection,
}

impl Database {
    pub fn connect(url: &str) -> Result<Self> {
        let connection = Connection::new(url)?;
        Ok(Self { connection })
    }

    pub fn query(&self, sql: &str) -> Result<Vec<Row>> {
        self.connection.execute(sql)
    }
}

