pub struct Storage {
    path: PathBuf,
}

impl Storage {
    pub fn new(path: PathBuf) -> Self {
        Self { path }
    }

    pub fn save(&self, data: &[u8]) -> Result<()> {
        std::fs::write(&self.path, data)
    }
}

